mapper.update:
UPDATE board
   SET subject = #{subject},
   	   content = #{content},
   	   writer = #{writer}
 WHERE idx = #{idx}

mapper.select:
select * from 
	(select @no := @no+1 as ROWNUM, A.* 
		from ( select * from board order by idx desc) as A, 
		(select @no := 0 ) as B ) as C
		 where C.ROWNUM between #{rowStart} and #{rowEnd};
